    private void btnRegresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegresarActionPerformed
        // Cortamos el texto para dejar solo la fecha (los primeros 10 caracteres "dd/mm/yyyy")
    if(Form_MenuPrincipal.horaVueloIda.length() > 10){
        Form_MenuPrincipal.horaVueloIda = Form_MenuPrincipal.horaVueloIda.substring(0, 10);
    }
        // Limpia los asientos de IDA seleccionados y restar sus precios del total
        util.DatosReserva.vuelosIda.clear();
        util.DatosReserva.total = 0; // Resetea costo
        Form_MenuPrincipal menu = new Form_MenuPrincipal();
        menu.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_btnRegresarActionPerformed

    private void btnElegirEconomicaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnElegirEconomicaActionPerformed
        // Precio ECONÃ“MICA
        int personas = Form_MenuPrincipal.numeroPersonas;
    Form_DatosPagar.totalPagar = 450.0 * personas; 

    Form_VuelosIdaEconomy vuelosEconomy = new Form_VuelosIdaEconomy();
    vuelosEconomy.setVisible(true);
    this.dispose();
    }//GEN-LAST:event_btnElegirEconomicaActionPerformed

    private void btnElegirPremiumActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnElegirPremiumActionPerformed
        // Precio PREMIUM
        int personas = Form_MenuPrincipal.numeroPersonas;
    Form_DatosPagar.totalPagar = 525.0 * personas;

    Form_VuelosIdaPremium vuelosPremium = new Form_VuelosIdaPremium();
    vuelosPremium.setVisible(true);
    this.dispose();

    }//GEN-LAST:event_btnElegirPremiumActionPerformed

